import './App.css';
import React, { useState, useEffect } from 'react';
import { Box, PageHeader, Grommet } from 'grommet';
import TaskBoard from './components/TaskBoard.js';

function App() {

	const [tasks, setTasks] = useState(null);
	const [isModalOpen, setIsModalOpen] = useState(false);
  const [status, setStatus] = useState('');
  const [description, setDescription] = useState('');

	const handleCreateTaskClick = () => {
		setIsModalOPen(true);
	}

	const handleAddTaskClick = () => {

			const status = window.prompt('Enter status:');
			const description = window.prompt('Enter description:');

			if (status && description) {

				const newTask = { status, description };

				fetch('http://localhost:8080/items', {
					method: 'POST',
					headers: {
						'Content-Type': 'application/json',
					},
					body: JSON.stringify(newTask),
				})
				.then(response => response.json())
        .then(data => {
          // Update the state with the new task
          setTasks([...tasks, data]);
        })
        .catch(error => console.error('Error creating task:', error));
			setStatus('');
      setDescription('');
      setIsModalOpen(false);
    } else {
      // Handle case where user cancels the prompt or inputs are empty
      console.log('Task creation canceled or invalid input.');
    }
  };


	useEffect(() => {
			// Fetch items from the server
			fetch('http://localhost:8080/items')
				.then(response => response.json())
				.then(data => setTasks(data))
				.catch(error => console.error('Error fetching items:', error));
  }, []);

  return (
    <div className="App">
			<Grommet>
				<PageHeader title="Task Manager"/>
				<Box margin="medium" gap="medium">
					<button onClick={handleAddTaskClick}> New Task </button>
					<TaskBoard tasks={tasks} />
				</Box>	
		</Grommet>
    </div>
  );
}

export default App;
