import { Box, Select, TextInput, FormField, Button } from 'grommet';
import React, { useState } from 'react';

export default function NewTask({ onClose, onCreateTask }) {
  const [status, setStatus] = useState('');
  const [description, setDescription] = useState('');

  const handleConfirmCreateTask = () => {
    if (status && description) {
      onCreateTask(status, description);
      onClose();
    } else {
      console.log('Invalid input.');
    }
  };

	return (
		<Box width={{max:"50%"}} align="center" justify="center">
      <FormField label="Status">
          <Select value={status} onChange={({option}) => setStatus(option)} options={['To Do', 'In Progress', 'Done']}/>
      </FormField>
      <FormField label="Description">
					<TextInput value={description} onChange={event => setDescription(event.target.value)} placeholder="type here" />
      </FormField>
      <Button label="Create Task" onClick={handleConfirmCreateTask}/>
      <Button label="Cancel" onClick={onClose}/>
    </Box>
  );
}
