import { Menu, Card, CardHeader, CardBody, CardFooter } from 'grommet';
import { MoreVertical } from 'grommet-icons';

export default function Task ( {task} ) {

	const deleteTask = (task) => {
		fetch('http://localhost:8080/items/${task.id}', {
					method: 'DELETE',
				})
				.then(response => response.json())
        .catch(error => console.error('Error creating task:', error));
};

	return (
		<Card gap="medium" elevation="large">
			<CardBody> {task.description} </CardBody>
			<CardFooter>
				    <Menu
							icon={<MoreVertical/>}
							items={[
								{ label: 'Move to'},
								{ label: 'Edit'},
								{ label: 'Delete', onClick : (task) => deleteTask(task)},
							]}
						/>
			</CardFooter>
		</Card>
	);
}
