import { Box } from 'grommet';
import Tasks from './Task.js';

export default function TaskBoard({tasks}) {
	if (!tasks) {
    return <p>Loading...</p>;
  }
	const validStatus = ["To Do", "In Progress", "Done"];

	const groupTasksByStatus = (tasks) => {
    return tasks.reduce((grouped, task) => {
      const status = task.status;
			if (validStatus.includes(status)) {
				if (!grouped[status]) {
					grouped[status] = [];
				}

				grouped[status].push(task);
			}
			return grouped;
    }, {});
  };

	const groupedTasks = groupTasksByStatus(tasks);
	console.log(groupedTasks);

	return (
		<Box round="medium" direction="row" gap="medium" justify="around" fill>

		{Object.entries(groupedTasks).map(([status, tasksForStatus]) => (
        <Box fill round="medium" elevation="large" key={status}>
          <h2>{status}</h2>
          <ul>
            {tasksForStatus.map(task => (
              <li key={task.id}><Task task={task}/></li>
            ))}
          </ul>
        </Box>
      ))}
		</Box>
  );
}
