import { MoreVertical } from 'grommet-icons';

export default function Task ( {task}, {deleteTask}, {editTask} ) {

	return (
		<Card gap="medium" elevation="large">
			<CardBody> {task.description} </CardBody>
			<CardFooter>
				    <DropButton
							icon={<MoreVertical/>}
							//dropContent={<Box><DropButton label='Move to'/><DropButton label='Edit'/><Button label="Delete"/></Box>}
						/>
			</CardFooter>
		</Card>
	);
}
