import { Menu, Card, CardHeader, CardBody, CardFooter } from 'grommet';
import { MoreVertical } from 'grommet-icons';

export default function Task ( {task} ) {

	const deleteTask = (task) => {
		console.log(task.id);
		fetch('http://localhost:8080/items/'+task.id.toString(), {
					method: 'DELETE',
					headers: {
						'Content-Type': 'application/json',
					},
				})
				.then(response => response)
        .catch(error => console.error('Error deleting task:', error));
};

	return (
		<Card gap="medium" elevation="large">
			<CardBody> {task.description} </CardBody>
			<CardFooter>
				    <Menu
							icon={<MoreVertical/>}
							items={[
								{ label: 'Move to'},
								{ label: 'Edit'},
								{ label: 'Delete', onClick : () => {deleteTask(task)}},
							]}
						/>
			</CardFooter>
		</Card>
	);
}
