import { TextArea, Button, Box, DropButton, Card, CardHeader, CardBody, CardFooter } from 'grommet';
import { MoreVertical } from 'grommet-icons';
import { useState , useEffect } from 'react';

export default function Task ( {task, onEditTask} ) {

	const [newDescription, setNewDescription] = useState('');

	const onClickEditTask = () => {
		onEditTask(task, newDescription);
	}; 


	return (
		<Card gap="medium" elevation="large">
			<CardBody> {task.description} </CardBody>
			<CardFooter>
				    <DropButton
							icon={<MoreVertical/>}
							dropContent={
								<Box>
									<DropButton label='Move to'/>
									<DropButton
										label='Edit'
										dropContent={
											<Box>
											<TextArea
												value={newDescription}
												onChange={event => setNewDescription(event.target.value)}
											/>
											<Button label='confirm' onClick={onClickEditTask}/>
											</Box>
										}
									/>
									<Button label='Delete'/>
								</Box>}
						/>
			</CardFooter>
		</Card>
	);
}
