import { Box } from 'grommet';

export default function TaskBoard(items) {
	if (!items) {
    return <p>Loading...</p>;
  }

	const groupItemsByStatus = (items) => {
    return items.reduce((grouped, item) => {
      const status = item.status;

      if (!grouped[status]) {
        grouped[status] = [];
      }

      grouped[status].push(item);
      return grouped;
    }, {});
  };

	const groupedItems = groupItemsByStatus(items);
	console.log(groupedItems);

	return (
		<Box round="medium" direction="row" gap="medium" justify="around" fill>
			<Box fill round="medium" elevation="large">
				To Do
			</Box>
			<Box fill justify="center" gap="medium" round="medium" elevation="large">
				In Progress
			</Box>
			<Box fill round="medium" elevation="large">
				Done
			</Box>
		</Box>
  );
}
